package basic_concepts;

public class Variable_study2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//Name
//Roll no
//Marks
//Grade
      String Name;     // variable declaration
      int Rollno;      // variable declaration
      float Marks;     // variable declaration
      char Grade;      // variable declaration
      
      Name="Rohit Kambale";  // variable initialization
      Rollno= 74;            // variable initialization
      Marks= 88.98f;         // variable initialization
      Grade='A';             // variable initialization
      
      System.out.println("My Name is" +Name);      // variable usage
      System.out.println("My Rollno is"+Rollno);   // variable usage
      System.out.println("My marks are"+Marks);    // variable usage
      System.out.println("My Grade is"+Grade);     // variable usage
	}

}
